import { Box, Theme } from "@mui/material";
import { makeStyles } from '@mui/styles'
import { ArticleCardProps } from "@/services/home/article.service";
const useStyles = makeStyles((theme: Theme) => ({
    content: {
        width: "100%",
        height: "300px",
        backgroundColor: "#fff",
        borderRadius: "10px",
        boxShadow: "0 0 10px rgba(0,0,0,.1)",
        padding: "20px",
        boxSizing: "border-box",
        margin: "20px 0",
        display: "flex",
        justifyContent: "space-between",
    },
    title: {
        fontSize: "20px",
        fontWeight: "bold",
        height: "40px",
    },
    textBox: {
        width: "70%",
        height: "100%", 
        border:"1px solid red",
        display: "flex",
        flexDirection: "column",
        justifyContent: "space-between",
    },
    imageBox: {
        width: "30%",
        height: "100%",
        border:"1px solid red"
    },
    time:{
        fontSize: "12px",
        color: "#999",
    }
}))

const ArticleCard = (props: ArticleCardProps) => {
    const { title, content, image, date } = props
    const classes = useStyles()
    return (
        <>
            <Box className={classes.content}>
                <Box className={classes.imageBox}></Box>
                <Box className={classes.textBox}>
                    <Box className={classes.title}>{title}</Box>
                    <Box className={classes.textContent}>{content}</Box>
                    <Box className={classes.time}>{date}</Box>
                </Box>
            </Box>
        </>
    )
}

export default ArticleCard
